ore\" prefix=\"c\" %>\r\n\r\n<page:pageContext id=\"pc\"/>\r\n\r\n<structure:componentPropertyValue id=\"rubrik\" propertyName=\"Headline\" useInheritance=\"false\"/>\r\n<structure:componentPropertyValue id=\"visaTumnagel\" propertyName=\"ShowThumbnail\" useInheritance=\"false\"/>\r\n\r\n<content:childContents id=\"components\" propertyName=\"Components\" includeFolders=\"true\"/>\r\n<content:contentSort id=\"components\" input=\"${components}\">\r\n	<content:sortContentVersionAttribute name=\"name\" ascending=\"false\"/>\r\n	<content:sortContentProperty name=\"publishDateTime\" ascending=\"false\"/>\r\n</content:contentSort>\r\n\r\n<c:forEach var=\"component\" items=\"${components}\">\r\n	<content:contentAttribute id=\"name\" contentId=\"${component.contentId}\" attributeName=\"Name\" disableEditOnSight=\"true\"/>\r\n	<c:if test=\"${not empty name}\">\r\n	    <h2><c:out value=\"${name}\" escapeXml=\"false\"/></h2>\r\n\r\n		<content:childContents id=\"subComponents\" contentId=\"${component.id}\"/>\r\n		<content:contentSort id=\"subComponents\" input=\"${subComponents}\">\r\n			<content:sortContentVersionAttribute name=\"name\" ascending=\"false\"/>\r\n		</content:contentSort>\r\n\r\n		<c:forEach var=\"component\" items=\"${subComponents}\">\r\n			<content:contentAttribute id=\"name\" contentId=\"${component.contentId}\" attributeName=\"Name\" disableEditOnSight=\"true\"/>\r\n			<c:if test=\"${not empty name}\">\r\n				<content:contentAttribute id=\"description\" contentId=\"${component.contentId}\" attributeName=\"Description\"/>\r\n			    <structure:pageUrl id=\"detaljSidaUrl\" propertyName=\"Detailpage\" contentId=\"${component.contentId}\"/>\r\n			    <div class=\"listItem\">\r\n			        <c:if test=\"${visaTumnagel == \'true\'}\">\r\n						<content:assetThumbnailUrl id=\"thumbnailUrl\" contentId=\"${component.contentId}\" width=\"50\" height=\"50\" assetKey=\"thumbnail\"/>\r\n						<c:if test=\"${empty thumbnailUrl}\">\r\n							<content:assetThumbnailUrl id=\"thumbnailUrl\" contentId=\"${pc.componentLogic.infoGlueComponent.contentId}\" width=\"50\" height=\"50\" assetKey=\"componentIcon\"/>\r\n						</c:if>\r\n						<img class=\"listImg\" src=\"<c:out value=\"${thumbnailUrl}\" escapeXml=\"false\"/>\"/>\r\n	            	</c:if>\r\n	                <p class=\"listRub\"><a href=\"<c:out value=\"${detaljSidaUrl}\" escapeXml=\"false\"/>\"><c:out value=\"${name}\" escapeXml=\"false\"/></a></p>\r\n	                <p class=\"listText\"><c:out value=\"${description}\" escapeXml=\"false\"/></p>\r\n			    </div>\r\n		    </c:if>\r\n		</c:forEach>	\r\n		\r\n    </c:if>\r\n</c:forEach>]]></Template><ComponentProperties><![CDATA[<?xml version=\'1.0\' encoding=\'UTF-8\'?><properties><property name=\'Components\' type=\'binding\' entity=\'Content\' multiple=\'false\' assetBinding=\'false\' allowedContentTypeDefinitionNames=\'Folder\' description=\'The binding to the article to show\'/><property name=\'Headline\' type=\'textfield\' entity=\'\' multiple=\'false\' assetBinding=\'false\' allowedContentTypeDefinitionNames=\'\' description=\'The list headline\'/><property name=\'Detailpage\' type=\'binding\' entity=\'SiteNode\' multiple=\'false\' assetBinding=\'false\' allowedContentTypeDefinitionNames=\'\' description=\'No description yet\'/><property name=\'ShowThumbnail\' type=\'select\' multiple=\'false\' assetBinding=\'false\' description=\'Show thumbnail\'><option name=\'\' value=\'\'/><option name=\'No\' value=\'false\'/><option name=\'Yes\' value=\'true\'/></property></properties>]]></ComponentProperties><Name><![CDATA[IG Component list component]]></Name><RelatedComponents><![CDATA[]]></RelatedComponents><Description><![CDATA[This component shows information about all components in the system]]></Description><Manual><![CDATA[]]></Manual><GroupName><![CDATA[Basic Pages]]></GroupName></attributes></article>','2008-01-20 18:05:44','Completely new site templates',0,1,301,1,'mattias'),(262,3,'<?xml version=\'1.0\' encoding=\'UTF-8\'?><article xmlns=\'x-schema:ArticleSchema.xml\'><attributes><Template><![CDATA[<%@ taglib uri=\"infoglue-page\" prefix=\"page\" %>\r\n<%@ taglib uri=\"infoglue-common\" prefix=\"common\" %>\r\n<%@ taglib uri=\"infoglue-structure\" prefix=\"structure\" %>\r\n<%@ taglib uri=\"infoglue-content\" prefix=\"content\" %>\r\n<%@ taglib uri=\"infoglue-management\" prefix=\"management\" %>\r\n<%@ taglib uri=\"infoglue-page\" prefix=\"tl\" %>\r\n<%@ taglib uri=\"http://java.sun.com/jstl/core\" prefix=\"c\" %>\r\n<%@ taglib uri=\"http://java.sun.com/jstl/fmt\" prefix=\"fmt\"%>\r\n<%@ taglib uri=\"http://jakarta.apache.org/taglibs/mailer-1.1\" prefix=\"mt\" %>\r\n\r\n<%@ page contentType=\"text/html; charset=UTF-8\" %>\r\n\r\n<page:pageContext id=\"pc\"/>\r\n\r\n<common:urlBuilder id=\"currentUrl\"/>\r\n<common:urlBuilder id=\"currentFullUrl\" fullBaseUrl=\"true\"/>\r\n\r\n<c:choose>\r\n	<c:when test=\"${param.contentId != null && param.contentId > -1}\">\r\n		<content:content id=\"articleContent\" contentId=\"${param.contentId}\"/>\r\n	</c:when>\r\n	<c:otherwise>\r\n		<content:content id=\"articleContent\" propertyName=\"Article\" useInheritance=\"false\"/>\r\n	</c:otherwise>\r\n</c:choose>\r\n\r\n<content:assetUrl id=\"headlineImageUrl\" propertyName=\"Article\" assetKey=\"header\" useInheritance=\"false\"/>\r\n<c:if test=\"${articleContent != null}\">\r\n	<content:contentVersion id=\"articleContentVersion\" content=\"${articleContent}\"/>\r\n	<c:if test=\"${articleContentVersion != null}\">\r\n		<management:principal id=\"articleModifier\" contentVersion=\"${articleContentVersion}\"/>\r\n		<content:contentAttribute id=\"title\" contentId=\"${articleContent.id}\" attributeName=\"Title\" useInheritance=\"false\"/>\r\n		<content:contentAttribute id=\"leadIn\" contentId=\"${articleContent.id}\" attributeName=\"Leadin\" useInheritance=\"false\"/>\r\n		<content:contentAttribute id=\"fullText\" contentId=\"${articleContent.id}\" attributeName=\"FullText\" useInheritance=\"false\"/>\r\n		<content:contentAttribute id=\"author\" contentId=\"${articleContent.id}\" attributeName=\"Author\" useInheritance=\"false\"/>\r\n	</c:if>\r\n</c:if>\r\n\r\n<c:if test=\'${articleContent == null}\'>\r\n	<c:set var=\"title\" value=\"Ingen artikel vald\"/>\r\n	<c:set var=\"leadIn\" value=\"Ingen artikel vald\"/>\r\n	<c:set var=\"fullText\" value=\"Ingen artikel vald\"/>\r\n</c:if>\r\n<%\r\nString fullText = (String)pageContext.getAttribute(\"fullText\");\r\nif(fullText.indexOf(\"<p>\") == -1)\r\n	pageContext.setAttribute(\"fullText\", \"<p>\" + fullText + \"</p>\");\r\n%>\r\n\r\n<c:if test=\"${headlineImageUrl != \'\'}\">\r\n	<img src=\"<c:out value=\"${headlineImageUrl}\"/>\" style=\"border: 1px solid #8499b3;\"/>\r\n</c:if>\r\n\r\n<h1><c:out value=\"${title}\" escapeXml=\"false\"/></h1>\r\n<c:out value=\"${fullText}\" escapeXml=\"false\"/>\r\n\r\n<c:if test=\"${pc.isInPageComponentMode}\">\r\n<p>\r\n<content:editOnSight id=\"editOnSightHTML\" contentId=\"${articleContent.id}\" attributeName=\"FullText\" html=\"Edit article\"/>\r\n<content:assignPropertyBinding id=\"assignHTML\" propertyName=\"Article\" html=\"Choose article\"/>\r\n<content:assignPropertyBinding id=\"assignNewHTML\" propertyName=\"Article\" html=\"Create new article\" createNew=\"true\"/>\r\n<c:choose>\r\n	<c:when test=\'${articleContent != null}\'>\r\n		<c:out value=\"${editOnSightHTML}\" escapeXml=\"false\"/> | \r\n	</c:when>\r\n	<c:otherwise>\r\n	</c:otherwise>\r\n</c:choose>\r\n<c:out value=\"${assignHTML}\" escapeXml=\"false\"/> | <c:out value=\"${assignNewHTML}\" escapeXml=\"false\"/>\r\n</p>\r\n</c:if>\r\n\r\n<ig:slot id=\"articleEnd\" inherit=\"false\"></ig:slot>\r\n\r\n\r\n<script type=\"text/javascript\">\r\n	function closeDiv(divId)\r\n	{\r\n		document.getElementById(divId).style.display = \"none\";\r\n	}\r\n	function openDiv(divId)\r\n	{\r\n		document.getElementById(divId).style.display = \"block\";\r\n	}\r\n</script>\r\n\r\n<c:set var=\"display\" value=\"none\"/>\r\n<c:if test=\"${param.sendTip == \'true\'}\"><c:set var=\"display\" value=\"block\"/></c:if>\r\n<div id=\"tipFriend\" style=\"display: <c:out value=\"${display}\"/>; position: absolute; top: 200px; width: 520px; height: 200px; background-color: white; padding: 5px; border: 1px solid #333;\">\r\n\r\n<% \r\n//TODO - gör om till taglib...\r\njavax.servlet.http.HttpServletRequest req = (javax.servlet.http.HttpServletRequest)pageContext.getRequest();\r\nString referer = req.getHeader(\"Referer\");\r\nif(referer != null)\r\n{\r\n	int index = referer.indexOf(req.getServerName());\r\n	boolean internalUrl = true;\r\n	if(index == -1)\r\n		internalUrl = false;\r\n		\r\n	pageContext.setAttribute(\"internalUrl\", new Boolean(internalUrl)); \r\n}\r\n%>\r\n\r\n<c:choose>\r\n	<c:when test=\"${internalUrl == true && param.sendTip == \'true\' && not empty param.receiverEmail && not empty param.senderEmail}\">\r\n		Trying to sent message with data:<br/> \r\n		SenderEmail: <c:out value=\"${param.senderEmail}\"/>\r\n		ReceiverEmail: <c:out value=\"${param.receiverEmail}\"/>\r\n		Tip URL: <c:out value=\"${param.tipUrl}\"/><br/>\r\n		SMTP Server: <c:out value=\"${pc.smtpServer}\"/><br/>\r\n		<c:set var=\"smtpServer\" value=\"${pc.smtpServer}\"/>\r\n		<mt:mail server=\'<%= (String)pageContext.getAttribute(\"smtpServer\") %>\'>\r\n		\r\n			<c:choose>\r\n	    	    <c:when test=\"${param.senderEmail != null && param.senderEmail != \'\'}\">\r\n					<mt:from><c:out value=\"${param.senderEmail}\"/></mt:from>\r\n	        	</c:when>\r\n        		<c:otherwise>\r\n					<mt:from>no-reply@infoglue.org</mt:from>\r\n        		</c:otherwise>\r\n        	</c:choose>\r\n\r\n			<mt:setrecipient type=\"to\"><c:out value=\"${param.receiverEmail}\"/></mt:setrecipient>\r\n			<mt:setrecipient type=\"cc\"><c:out value=\"${param.senderEmail}\"/></mt:setrecipient>\r\n			<mt:setrecipient type=\"cc\">mattias.bogeblad@modul1.se</mt:setrecipient>\r\n			<mt:subject>A friend wants you to visit this page</mt:subject>\r\n			<mt:message type=\"html\" charset=\"utf-8\">\r\n				<div>\r\n				<a href=\"mailto:\"<c:out value=\"${param.senderEmail}\"/>\"><c:out value=\"${param.senderEmail}\"/></a><br />\r\n				A friend wants you to check this url<br /><br />\r\n				<a href=\"<c:out value=\"${param.tipUrl}\"/>\"><c:out value=\"${param.tipUrl}\"/></a>\r\n				<c:if test=\"${not empty param.message}\">\r\n				<br /><br />\r\n				Comment:<br/>\r\n				<%\r\n				String message = pageContext.getRequest().getParameter(\"message\");\r\n				message = message.replaceAll(\"\\n\", \"<br/>\"); \r\n				pageContext.setAttribute(\"message\", message);\r\n				%>\r\n				<div><pre><c:out value=\"${message}\" escapeXml=\"false\"/><br/></pre></div>\r\n				</c:if>\r\n				<br /><br /><br />\r\n				<hr>\r\n				Do not reply to this message - it was automatically sent from the infoglue.org site.\r\n				</div></mt:message>\r\n\r\n			<mt:send>\r\n				<mt:error id=\"err\">\r\n					Error sending mail... <c:out value=\"${err}\"/>\r\n		       	</mt:error>\r\n			</mt:send>\r\n		</mt:mail>\r\n		\r\n		<c:set var=\"messageSent\" value=\"true\"/>\r\n	</c:when>\r\n	<c:otherwise>\r\n\r\n		<form name=\"tipForm\" action=\"<c:out value=\"${currentUrl}\"/>\" method=\"post\">\r\n			<input type=\"hidden\" name=\"sendTip\" value=\"true\"/>\r\n			<input type=\"hidden\" name=\"tipUrl\" value=\"<c:out value=\"${currentFullUrl}\"/>\"/>\r\n		\r\n			<fieldset>\r\n				<legend>Tip a friend</legend>\r\n				<p>\r\n					<label for=\"senderEmail\">Your email <c:if test=\"${empty param.senderEmail}\"><span style=\"color:red;\">*</span></c:if></label>\r\n					<input id=\"senderEmail\" type=\"text\" name=\"senderEmail\"/>\r\n				</p>\r\n				<p>\r\n					<label for=\"receiverEmail\">Your friends email <c:if test=\"${empty param.receiverEmail}\"><span style=\"color:red;\">*</span></c:if></label>\r\n					<input id=\"receiverEmail\"  type=\"text\" name=\"receiverEmail\"/>\r\n				</p>\r\n				<p>\r\n					<label for=\"message\">Message <c:if test=\"${empty param.message}\"><span style=\"color:red;\">*</span></c:if></label>\r\n					<textarea id=\"message\" name=\"message\" rows=\"5\" cols=\"30\"></textarea>\r\n				</p>\r\n				<p>&nbsp;</p>\r\n				<p>\r\n					<input class=\"submitButton\" type=\"submit\" value=\"Submit\"/>\r\n					<input class=\"submitButton\" type=\"button\" onclick=\"closeDiv(\'tipFriend\');\" value=\"Close\"/>\r\n				</p>\r\n			</fieldset>\r\n		</form>\r\n	\r\n	</c:otherwise>\r\n</c:choose>\r\n\r\n</div>\r\n\r\n<c:set var=\"display\" value=\"none\"/>\r\n<c:if test=\"${param.sendComment == \'true\'}\"><c:set var=\"display\" value=\"block\"/></c:if>\r\n\r\n<div id=\"commentArticle\" style=\"display: <c:out value=\"${display}\"/>; position: absolute; top: 200px; width: 520px; height: 200px; background-color: white; padding: 5px; border: 1px solid #333;\">\r\n\r\n<c:choose>\r\n	<c:when test=\"${internalUrl == true && param.sendComment == \'true\' && not empty param.commentSenderEmail}\">\r\n		Trying to sent comment with data:<br/> \r\n		SenderEmail: <c:out value=\"${param.commentSenderEmail}\"/><br/>\r\n		The comment will be published after review\r\n		\r\n		<c:set var=\"smtpServer\" value=\"${pc.smtpServer}\"/>\r\n		<mt:mail server=\'<%= (String)pageContext.getAttribute(\"smtpServer\") %>\'>\r\n		\r\n			<c:choose>\r\n	    	    <c:when test=\"${param.commentSenderEmail != null && param.commentSenderEmail != \'\'}\">\r\n					<mt:from><c:out value=\"${param.senderEmail}\"/></mt:from>\r\n	        	</c:when>\r\n        		<c:otherwise>\r\n					<mt:from>no-reply@infoglue.org</mt:from>\r\n        		</c:otherwise>\r\n        	</c:choose>\r\n\r\n			<mt:setrecipient type=\"to\">bogeblad@yahoo.com</mt:setrecipient>\r\n			<mt:setrecipient type=\"cc\"><c:out value=\"${param.commentSenderEmail}\"/></mt:setrecipient>\r\n			<mt:subject>A comment was added - <c:out value=\"${param.subject}\"/></mt:subject>\r\n			<mt:message type=\"html\" charset=\"utf-8\">\r\n				<div>\r\n				Jag vill testa detta... skicka din kommentar\r\n				<a href=\"mailto:\"<c:out value=\"${param.commentSenderEmail}\"/>\"><c:out value=\"${param.commentSenderEmail}\"/></a><br />\r\n				A person wants to add a comment to this url:<br /><br />\r\n				<a href=\"<c:out value=\"${param.commentUrl}\"/>\"><c:out value=\"${param.commentUrl}\"/></a>\r\n				<c:if test=\"${not empty param.comment}\">\r\n				<br /><br />\r\n				Comment:<br/>\r\n				<%\r\n				String comment = pageContext.getRequest().getParameter(\"comment\");\r\n				comment = comment.replaceAll(\"\\n\", \"<br/>\"); \r\n				pageContext.setAttribute(\"comment\", comment);\r\n				%>\r\n				<div><pre><c:out value=\"${comment}\" escapeXml=\"false\"/><br/></pre></div>\r\n				</c:if>\r\n				<br /><br /><br />\r\n				<hr>\r\n				Do not reply to this message - it was automatically sent from the infoglue.org site.\r\n				</div></mt:message>\r\n\r\n			<mt:send>\r\n				<mt:error id=\"err\">\r\n					Error sending mail... <c:out value=\"${err}\"/>\r\n		       	</mt:error>\r\n			</mt:send>\r\n		</mt:mail>\r\n		\r\n		<c:set var=\"messageSent\" value=\"true\"/>\r\n	</c:when>\r\n	<c:otherwise>\r\n\r\n		<form name=\"commentArticleForm\" action=\"<c:out value=\"${currentUrl}\"/>\" method=\"post\">\r\n			<input type=\"hidden\" name=\"sendComment\" value=\"true\"/>\r\n			<input type=\"hidden\" name=\"commentUrl\" value=\"<c:out value=\"${currentFullUrl}\"/>\"/>\r\n			<input type=\"hidden\" name=\"commentContentId\" value=\"${commentContentId}\"/>\r\n		\r\n			<fieldset>\r\n				<legend>Comment article</legend>\r\n				<p>\r\n					<label for=\"subject\">Subject <c:if test=\"${empty param.subject}\"><span style=\"color:red;\">*</span></c:if></label>\r\n					<input id=\"subject\" type=\"text\" name=\"subject\"/>\r\n				</p>\r\n				<p>\r\n					<label for=\"commentSenderEmail\">Your email <c:if test=\"${empty param.commentSenderEmail}\"><span style=\"color:red;\">*</span></c:if></label>\r\n					<input id=\"commentSenderEmail\" type=\"text\" name=\"commentSenderEmail\"/>\r\n				</p>\r\n				<p>\r\n					<label for=\"comment\">Comment <c:if test=\"${empty param.comment}\"><span style=\"color:red;\">*</span></c:if></label>\r\n					<textarea id=\"comment\"  name=\"comment\" rows=\"5\" cols=\"30\"></textarea>\r\n				</p>\r\n				<p>&nbsp;</p>\r\n				<p>\r\n					<input class=\"submitButton\" type=\"submit\" value=\"Submit\"/>\r\n					<input class=\"submitButton\" type=\"button\" onclick=\"closeDiv(\'commentArticle\');\" value=\"Close\"/>\r\n				</p>\r\n			</fieldset>\r\n		</form>\r\n\r\n	</c:otherwise>\r\n</c:choose>\r\n\r\n</div>\r\n\r\n<div id=\"printField\">\r\n	<ul>\r\n        <li class=\"print\"><a href=\"javascript:window.print();\" title=\"Print page\">Print</a></li>\r\n        <li class=\"tellaFriend\"><a href=\"javascript:openDiv(\'tipFriend\');\" title=\"Tip a friend\">Tip a friend</a></li>\r\n        <li class=\"comment\"><a href=\"javascript:openDiv(\'commentArticle\');\" title=\"Comment article\">Comment</a></li>\r\n	</ul>\r\n</div>\r\n\r\n<content:contentAttribute contentId=\"${articleContent.id}\" id=\"canComment\" attributeName=\"CanComment\" disableEditOnSight=\"true\"/>\r\n<c:if test=\"${canComment==\'true\'}\">\r\n  <common:include relationAttributeName=\"RelatedComponents\" contentName=\"IGC Article comment component\"/>\r\n</c:if>\r\n\r\n<%--\r\n<?xml version=\'1.0\' encoding=\'UTF-8\'?><properties><property name=\'Article\' type=\'binding\' entity=\'Content\' multiple=\'false\' allowedContentTypeDefinitionNames=\'Article\' description=\'No description yet\'/></properties>\r\n--%>]]></Template><ComponentProperties><![CDATA[<?xml version=\'1.0\' encoding=\'UTF-8\'?><properties><property name=\'Article\' type=\'binding\' entity=\'Content\' multiple=\'false\' assetBinding=\'false\' allowedContentTypeDefinitionNames=\'Article,Article\' description=\'No description yet\'/></properties>]]></ComponentProperties><Name><![CDATA[IG Standard artikelkomponent]]></Name><RelatedComponents><![CDATA[]]></RelatedComponents><Description><![CDATA[Denna komponent är en standardkomponent för att visa upp en enkel artikel. Har en toolbar längst ner för print, tipsa osv.]]></Description><Manual><![CDATA[<p>Detta &auml;r en relativt enkel artikelkomponent. Den har en property som heter Article som pekar ut artikeln som skall visas. Sen har den ett par trevliga l&auml;nkar i struktur-vyn som g&ouml;r att det &auml;r v&auml;ldigt smidigt att arbeta med den som redakt&ouml;r. Komponenten har dessutom print, tipsa och kommentera-funktionalitet inbyggd.</p><p><img vspace=\"5\" hspace=\"5\" border=\"1\" src=\"$templateLogic.getInlineAssetUrl(302, \"fullDec\")\" alt=\"\" /><img vspace=\"5\" hspace=\"5\" border=\"1\" src=\"$templateLogic.getInlineAssetUrl(302, \"full\")\" alt=\"\" /></p><p>Bilderna ovan visar exempel p&aring; hur komponenten kan se ut. Den f&ouml;rsta visar l&auml;nkarna som bara syns i edit-l&auml;get.</p>]]></Manual><GroupName><![CDATA[Basic Pages]]></GroupName></attributes></article>','2008-01-21 12:45:19','Ändrade lite',0,1,302,1,'mattias'),(263,3,'<?xml version=\'1.0\' encoding=\'UTF-8\'?><article xmlns=\'x-schema:ArticleSchema.xml\'><attributes><Name><![CDATA[Component detail component]]></Name><Description><![CDATA[Detta är en komponent som visar detaljer om en komponent inklusive en möjlighet att ladda ner komponentens kod.]]></Description><GroupName><![CDATA[Basic Pages]]></GroupName><Template><![CDATA[<%@ taglib uri=\"infoglue-page\" prefix=\"page\" %>\n<%@ taglib uri=\"infoglue-common\" prefix=\"common\" %>\n<%@ taglib uri=\"infoglue-structure\" prefix=\"structure\" %>\n<%@ taglib uri=\"infoglue-content\" prefix=\"content\" %>\n<%@ taglib uri=\"infoglue-management\" prefix=\"management\" %>\n<%@ taglib uri=\"infoglue-page\" prefix=\"tl\" %>\n<%@ taglib uri=\"http://java.sun.com/jstl/core\" prefix=\"c\" %>\n<%@ taglib uri=\"http://java.sun.com/jstl/fmt\" prefix=\"fmt\"%>\n\n<page:pageContext id=\"pc\"/>\n<page:deliveryContext id=\"dc\" evaluateFullPage=\"false\"/>\n\n<c:choose>\n	<c:when test=\"${param.contentId != null && param.contentId > -1}\">\n		<content:content id=\"articleContent\" contentId=\"${param.contentId}\"/>\n	</c:when>\n	<c:otherwise>\n		<content:content id=\"articleContent\" propertyName=\"Komponent\" useInheritance=\"false\"/>\n	</c:otherwise>\n</c:choose>\n\n<c:if test=\"${articleContent != null}\">\n	<content:contentVersion id=\"articleContentVersion\" content=\"${articleContent}\"/>\n	<c:if test=\"${articleContentVersion != null}\">\n		<management:principal id=\"articleModifier\" contentVersion=\"${articleContentVersion}\"/>\n		<content:contentAttribute id=\"name\" contentId=\"${articleContent.id}\" attributeName=\"Name\" useInheritance=\"false\"/>\n		<content:contentAttribute id=\"description\" contentId=\"${articleContent.id}\" attributeName=\"Description\" useInheritance=\"false\"/>\n		<content:contentAttribute id=\"manual\" contentId=\"${articleContent.id}\" attributeName=\"Manual\" useInheritance=\"false\" parse=\"true\"/>\n		<content:contentAttribute id=\"template\" contentId=\"${articleContent.id}\" attributeName=\"Template\" useInheritance=\"false\" disableEditOnSight=\"true\"/>\n		<content:assetUrl id=\"exportUrl\" contentId=\"${articleContent.id}\" assetKey=\"componentExport\" useDownloadAction=\"true\"/>\n		<content:contentAttribute id=\"properties\" contentId=\"${articleContent.id}\" attributeName=\"ComponentProperties\" useInheritance=\"false\" disableEditOnSight=\"true\"/>\n	</c:if>\n</c:if>\n\n<c:if test=\"${articleContent == null}\">\n	<c:set var=\"name\" value=\"empty\"/>\n	<c:set var=\"description\" value=\"empty\"/>\n	<c:set var=\"manual\" value=\"empty\"/>\n	<c:set var=\"template\" value=\"empty\"/>\n	<c:set var=\"template\" value=\"empty\"/>\n</c:if>\n\n\n<h1><c:out value=\"${name}\" escapeXml=\"false\"/></h1>\n<p><c:out value=\"${description}\" escapeXml=\"false\"/></p>\n<p><c:out value=\"${manual}\" escapeXml=\"false\"/></p>			\n<h3>Code</h3>\n\n<common:transformText id=\"newTemplate\" text=\"${template}\" replaceLineBreaks=\"true\" lineBreakReplacer=\"<br/>\" htmlEncode=\"true\"/>\n<common:transformText id=\"newTemplate\" text=\"${newTemplate}\" prefix=\"<html><body style=\'font-family: Courier; font-size: 10px;\'><pre>\" suffix=\"</pre></body></html>\"/>\n\n<content:assetUrlFromString id=\"componentCodeUrl\" data=\"${newTemplate}\" fileNamePrefix=\"${pc.componentLogic.infoGlueComponent.name}\" fileNameSuffix=\"html\"/>\n       \n<IFRAME style=\"height: 300px; width: 700px; margin-bottom: 5px;\" src=\"<c:out value=\"${componentCodeUrl}\"/>\">\n</IFRAME>	\n\n<c:set var=\"propertiesXSLT\">\n	<xsl:stylesheet version=\"2.0\" xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\">\n	  	<xsl:output method=\"xml\" version=\"1.0\" encoding=\"iso-8859-1\" indent=\"yes\"/>\n	    \n		<xsl:template match=\"/\"> \n			<table style=\"border: 1px solid #333333; width: 700px; margin-bottom: 5px;\">\n		     	<tr bgcolor=\"#cccccc\">\n		       		<th>Name</th>\n		       		<th>Type</th>\n		       		<th>Entity</th>\n		       		<th>Multiple</th>\n		       		<th>AssetBinding</th>\n		       		<th>Allowed CTD Names</th>\n		       		<th>Description</th>\n		     	</tr>\n	   	    	<xsl:for-each select=\"//property\">\n		     	<tr>\n		      		<td><xsl:value-of select=\"@name\"/></td>\n		      		<td><xsl:value-of select=\"@type\"/></td>\n		      		<td><xsl:value-of select=\"@entity\"/></td>\n		      		<td><xsl:value-of select=\"@multiple\"/></td>\n		      		<td><xsl:value-of select=\"@assetBinding\"/></td>\n		      		<td><xsl:value-of select=\"@allowedContentTypeDefinitionNames\"/></td>\n		      		<td><xsl:value-of select=\"@description\"/></td>\n		     	</tr>\n	    		</xsl:for-each>\n		   	</table>\n	  	</xsl:template>\n	</xsl:stylesheet>\n</c:set>\n<c:catch var=\"error\">\n<c:choose>\n	<c:when test=\"${empty properties}\">\n		<c:set var=\"propertiesHTML\" value=\"No properties defined for this component\"/>\n	</c:when>\n	<c:otherwise>\n		<common:XSLTransform id=\"propertiesHTML\" xmlString=\"${properties}\" styleString=\"${properties