defined\'}\">\r\n		<c:set var=\"descriptionName\" value=\"Leadin\"/>\r\n	</c:if>\r\n	<c:forEach var=\"newsItem\" items=\"${news}\" varStatus=\"count\">\r\n		<content:contentAttribute id=\"navigationTitle\" contentId=\"${newsItem.id}\" attributeName=\"${titleName}\" disableEditOnSight=\"true\"/>\r\n		<content:contentAttribute id=\"leadIn\" contentId=\"${newsItem.id}\" attributeName=\"${descriptionName}\" disableEditOnSight=\"true\"/>\r\n		<structure:pageUrl id=\"url\" contentId=\"${newsItem.id}\" propertyName=\"DetailPage\"/>\r\n		<common:rssFeedEntry title=\"${navigationTitle}\" link=\"${url}\" description=\"${leadIn}\" publishedDate=\"${newsItem.publishDateTime}\"/>\r\n	</c:forEach>\r\n</common:rssFeed>\r\n<c:out value=\"${rss}\" escapeXml=\"false\"/>]]></Template><ComponentProperties><![CDATA[<?xml version=\'1.0\' encoding=\'UTF-8\'?><properties><property name=\'TitleAttributeName\' type=\'textfield\' entity=\'\' multiple=\'false\' allowedContentTypeDefinitionNames=\'\' description=\'The attribute shown as title\'/><property name=\'DescriptionAttributeName\' type=\'textfield\' entity=\'\' multiple=\'false\' allowedContentTypeDefinitionNames=\'\' description=\'The attribute shown as description\'/><property name=\'FeedType\' type=\'select\' multiple=\'false\' description=\'What type of feed to expose, possible are: rss_0.9, rss_0.91, rss_0.92, rss_0.93, rss_0.94, rss_1.0, rss_2.0 atom_0.3\'><option name=\'RSS 1.0\' value=\'rss_1.0\'/><option name=\'RSS 2.0\' value=\'rss_2.0\'/><option name=\'Atom 0.3\' value=\'atom_0.3\'/><option name=\'Atom 1.0\' value=\'atom_1.0\'/></property><property name=\'Folder\' type=\'binding\' entity=\'Content\' multiple=\'false\' allowedContentTypeDefinitionNames=\'\' description=\'\'/><property name=\'Detail Page\' type=\'binding\' entity=\'SiteNode\' multiple=\'false\' allowedContentTypeDefinitionNames=\'\' description=\'The detail page\'/></properties>]]></ComponentProperties><Name><![CDATA[IG Dynamic RSS component]]></Name><RelatedCo